    /**
     * 双循环+快速失败，效率不高。
     * 执行用时：800 ms, 在所有 Java 提交中击败了5.26% 的用户
     * 内存消耗：38.2 MB, 在所有 Java 提交中击败了85.06% 的用户
     */
    public int findLength(int[] nums1, int[] nums2) {
        int max = 0;
        for (int i = 0; i < nums1.length - max; i++) {
            for (int j = 0; j < nums2.length - max; j++) {
                max = Math.max(match(nums1, nums2, i, j), max);
            }
        }
        return max;
    }

    private int match(int[] nums1, int[] nums2, int s1, int s2) {
        if (s1 >= nums1.length) {
            return 0;
        }
        if (s2 >= nums2.length) {
            return 0;
        }
        if (nums1[s1] != nums2[s2]) {
            return 0;
        } else {
            return match(nums1, nums2, ++s1, ++s2) + 1;
        }
    }

